// Magnetosphere.min.js
function Magnetosphere(particle, strength, color) {
    this.particle = particle; // The particle generating the magnetosphere
    this.strength = strength; // Influences the size/intensity of the magnetosphere
    this.color = color || "rgba(0, 100, 255, 0.2)"; // Default color (blue, semi-transparent)

    // Method to draw the magnetosphere
    this.draw = function(context, zoomLevel) {
        context.beginPath();
        // A simple magnetosphere could be a circle or an ellipse around the particle
        // The radius could be proportional to its strength and the particle's mass/radius
        var radius = this.particle.radius * 10 + this.strength / 100; // Example calculation
        context.arc(this.particle.x, this.particle.y, radius, 0, 2 * Math.PI);
        context.closePath();
        context.fillStyle = this.color;
        context.fill();
        context.strokeStyle = "rgba(0, 100, 255, 0.8)"; // Border color
        context.lineWidth = 1 / zoomLevel; // Scale line width with zoom
        context.stroke();
    };
}