function Particle(t,s,h,i,o,a,n){
    this.mass=t;
    this.x=s;
    this.y=h;
    this.vx=i;
    this.vy=o;
    this.ax=0;
    this.ay=0;
    this.type=a||"matter";
    this.name=n||"";
    this.color=[];
    
    // NEW: Black hole properties
    this.isBlackHole = (a === "blackhole");
    if (this.isBlackHole) {
        this.color[0] = 0;
        this.color[1] = 0;
        this.color[2] = 0;
        this.radius = Math.sqrt(t) / 50; // Radius based on mass
        this.eventHorizonRadius = this.radius * 2; // Event horizon is twice the radius
        this.name = "Black Hole"; // Override name
    } else {
        this.type=="matter"? (this.color[0]=255,this.color[1]=Math.round(256/(1+Math.pow(t/1e5,1))),this.color[2]=Math.round(256/(1+Math.pow(t/1e4,1)))) : (this.color[0]=Math.round(256/(1+Math.pow(t/1e4,1))),this.color[1]=Math.round(256/(1+Math.pow(t/1e5,1))),this.color[2]=255);
        this.radius=Math.log(Math.E+t/1e3);
    }
    
    this.color[3]=(this.color[0]<<16|this.color[1]<<8|this.color[2]).toString(16);

    // Array para almacenar el historial de posiciones de la órbita
    this.path = [];
    this.pathLength = 10000; // Número máximo de puntos en la órbita
}
